(526, '_transient_redux-extensions-fetch', 'a:14:{s:9:"accordion";a:7:{s:2:"id";i:2598;s:5:"title";s:9:"Accordion";s:7:"created";s:19:"2015-03-26 21:43:11";s:7:"updated";s:19:"2015-04-02 21:29:00";s:7:"excerpt";s:129:"The Redux Accordion extension is an improved way to group like items in your options panel, inside a collapsible accordion field.";s:3:"url";s:46:"http://reduxframework.com/extension/accordion/";s:5:"thumb";s:75:"http://reduxframework.com/wp-content/uploads/edd/2015/03/accordion_logo.png";}s:8:"repeater";a:7:{s:2:"id";i:2147;s:5:"title";s:8:"Repeater";s:7:"created";s:19:"2015-01-14 00:29:40";s:7:"updated";s:19:"2015-03-26 02:44:49";s:7:"excerpt";s:140:"Give your users the power of infinite flexibility with dynamic, repeatable fields. Use any Redux field and customize your user''s experience.";s:3:"url";s:45:"http://reduxframework.com/extension/repeater/";s:5:"thumb";s:69:"http://reduxframework.com/wp-content/uploads/edd/2015/01/repeater.png";}s:15:"social-profiles";a:7:{s:2:"id";i:2049;s:5:"title";s:15:"Social Profiles";s:7:"created";s:19:"2014-12-16 05:40:43";s:7:"updated";s:19:"2015-03-26 02:45:15";s:7:"excerpt";s:153:"Give your users the ability to add social profiles (icons with links to their social pages) from one central field in your Redux Framework options panel.";s:3:"url";s:52:"http://reduxframework.com/extension/social-profiles/";s:5:"thumb";s:71:"http://reduxframework.com/wp-content/uploads/edd/2014/12/social_ext.png";}s:9:"js-button";a:7:{s:2:"id";i:1965;s:5:"title";s:9:"JS Button";s:7:"created";s:19:"2014-11-27 03:57:50";s:7:"updated";s:19:"2015-03-26 02:45:38";s:7:"excerpt";s:131:"Give your users the ability to run JavaScript functions manually from within the Redux Framework options panel via button clicks.  ";s:3:"url";s:46:"http://reduxframework.com/extension/js-button/";s:5:"thumb";s:70:"http://reduxframework.com/wp-content/uploads/edd/2014/11/js_button.png";}s:11:"multi-media";a:7:{s:2:"id";i:1963;s:5:"title";s:11:"Multi Media";s:7